const smlblb = document.getElementById("bulb-small");
const lgblb = document.getElementById("bulb-large");


var tl = anime.timeline({
    easing: 'easeOutExpo',
    duration: 4600
});
tl.add({
    targets: "#herotxt-svg g path",
    strokeDashoffset: [anime.setDashoffset, 0],
    easing: 'linear',
    duration: 3600,
    fill: '#9098bd',
    delay: function(el, i) { return i * 250 },
    direction: 'normal',
    loop: false,
})
anime({
    targets: "div #bulb-small",
    translateY: 320,
    easing: 'easeOutElastic(1, .8)',
    duration: 2000,
    delay: 200
    // top: '0%'
})
anime({
    targets: "div #circle-svg",
    opacity: 1,
    duration: 500,
    easing: 'linear'
})
anime({
    targets: "div #bulb-large",
    translateY: 520,
    easing: 'easeOutElastic(1, .8)',
    duration: 2000,
    // top: '0%'
})
anime({
    targets: '.cls-1',
    fill: '#2A63AC',
    easing: 'linear',
    duration: 200,
    delay: 600
})
anime({
    targets: '.cls-2',
    fill: '#F73C57',
    easing: 'linear',
    duration: 200,
    delay: 700
})
anime({
    targets: '.cls-3',
    fill: '#9098bd',
    easing: 'linear',
    duration: 200,
    delay: 800
})
anime({
    targets: "svg #cls-oo",
    easing: 'easeOutInBounce',
    // easing: 'easeOutElastic(1, .4)',
    fill: '#8664ad',
    duration: 400,

})
anime({
    targets: "svg #cls-ii",
    easing: 'easeInOutBounce',
    fill: '#f73c57',
    duration: 400,

})

let down = anime({
    targets: "#cls-oo",
    fill: '#f73c57',
    duration: 400,
    easing:'linear',
    autoplay: false
})
let up = anime({
    targets: "#cls-ii",
    fill: '#8664ad',
    duration: 400,
    easing:'linear',
    autoplay: false
})
let down_rev = anime({
    targets: "#cls-oo",
    fill: '#8664ad',
    duration: 400,
    easing:'linear',
    autoplay: false
})
let up_rev = anime({
    targets: "#cls-ii",
    fill: '#f73c57',
    duration: 400,
    easing:'linear',
    autoplay: false
})

var lastScrollTop = 0;

window.addEventListener("scroll", function(){ 
   var st = window.pageYOffset || document.documentElement.scrollTop;
   if (st > lastScrollTop){
        down.play();
        up.play()
        console.log("down");
   } else if(st < lastScrollTop) {
       up_rev.play();
       down_rev.play();
       console.log("up");
   }
   lastScrollTop = st <= 0 ? 0 : st; // For Mobile or negative scrolling
}, false);

anime({
    targets: "div #download-btn a",
    easing: "easeInOutQuad",
    opacity: 1,
    duration: 200,
    delay: 100
})


anime({
    targets: "div #download-btn a i",
    rotate: 360,
    easing: 'easeInOutSine',
    duration: 800,
    delay:200
})

